import { Component } from '@angular/core';
import { Observable } from 'rxjs';
import { CatService } from '../cat.service';
import { User } from '../models/user';
import { UserService } from '../user.service';

@Component({
  selector: 'name-that-cat-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {
  readonly user$: Observable<User | undefined> = this.userService.getCurrentUser();

  constructor(
    private readonly userService: UserService,
    private readonly catService: CatService
  ) { }

  nextCat() {
    this.catService.getAnotherCat().subscribe();
  }

  switchUser() {
    this.userService.clearCurrentUser();
  }

}
<div class="h-auto text-center bg-indigo-300 rounded-3xl m-10 p-5">
    <div class="header-title align-text-bottom">Name That Cat</div>
    <div class="flex justify-around w-full my-2" *ngIf="user$ | async">
        <button mat-raised-button color="warn" (click)="switchUser()">SWITCH USERS</button>
        <button mat-raised-button color="primary" (click)="nextCat()">GET ANOTHER CAT</button>
    </div>
</div>
